#!/usr/bin/python3from pprint import pprintimport mathimport __def__._0_read as rimport __def__._0_transform as trandef comp_lfm_sg(COMP_DATALFM_IN):    print('_1_COMP_LFM_SG.comp_lfm_sg')    Coef_Sgat_re_r = []    Coef_Sgat_re_f = []    Coef_Sgat_im_r = []    Coef_Sgat_im_f = []    name_file_coef_sgat = ['data/CoefSG/lfm_re_rise.dat',                           'data/CoefSG/lfm_re_fall.dat',                           'data/CoefSG/lfm_im_rise.dat',                           'data/CoefSG/lfm_im_fall.dat']    Coef_Sgat = [Coef_Sgat_re_r, Coef_Sgat_re_f, Coef_Sgat_im_r, Coef_Sgat_im_f]    '''вычитка коефициентов из файлов'''    r.READ_float_fileS(name_file_coef_sgat,Coef_Sgat)    Coef_Sgat_re_r = Coef_Sgat_re_r[::2]    Coef_Sgat_re_f = Coef_Sgat_re_f[::2]    Coef_Sgat_im_r = Coef_Sgat_im_r[::2]    Coef_Sgat_im_f = Coef_Sgat_im_f[::2]    COMP_Coef_Sgat = [complex(a, b) for a, b in zip(Coef_Sgat_re_r, Coef_Sgat_im_r)]    coef = COMP_Coef_Sgat    coef_Sgat = iter(coef)    #i = iter(coef)    i = iter(COMP_DATALFM_IN)    COMP_DATAOUT_LFM = []    Sum = complex()    n_700 = 1600    n=0    while n_700:        try:            f = next(i)*next(coef_Sgat)            Sum += f        except StopIteration:            n_700 -= 1            n += 1            #pprint(Sum)            Sum *= 0.2            COMP_DATAOUT_LFM.append(Sum)            coef_Sgat = iter(coef)            i         = iter(COMP_DATALFM_IN[n:])            Sum = complex()            continue        except:            break    #pprint(COMP_DATAOUT_LFM)    aut = data_lfm = tran.from_COMP_in_RE_IM(COMP_DATAOUT_LFM)    #pprint(aut)    MODUL = []    for i in COMP_DATAOUT_LFM:        M = 20 * math.log10(abs(i))        #print(M)        #M = abs(i)        MODUL.append(M)    #print('COMP_DATAOUT_LFM',type(COMP_DATAOUT_LFM))    return COMP_DATAOUT_LFM, aut[0::2], aut[1::2], MODUL