import __def__._0_read as rimport __def__._1_COMP_FKM_SG as sg_fkmimport __def__._0_transform as tranimport __def__._1_COMP_LFM_SG as COMP_LFMimport mathdef POLNAYA(name_file):    print('POLNAYA')    DATAIN = r.READ_FILE_HEX_16r_shapka20(name_file)    #DATAIN = r.READ_FILE_HEX_16r_bez_sh(name_file)    COMP_DATAIN = DATAIN[0][:10050]    Shapka = DATAIN[1]    Kontr_word = bin(int(Shapka[1],16))    KPP = int(Kontr_word[2:4:],2)    #print(KPP)    COMP_DATAIN_1K = COMP_DATAIN[0::5]    COMP_DATAIN_2K = COMP_DATAIN[1::5]    COMP_DATAIN_3K = COMP_DATAIN[2::5]    COMP_DATAIN_4K = COMP_DATAIN[3::5]    COMP_DATAIN_DOP = COMP_DATAIN[4::5]    coef_fkm = 5.3452,5.8116,6.0749,6.3923,5.3452,6.3278,6.8020,7.7305    coef_lfm = 1.0000,1.0872,1.1365,1.1959,1.0000,1.1838,1.1273,1.4462    COMP_DATAIN_OSN = []    coef_fkm = coef_fkm[KPP]*1.0    coef_lfm = coef_lfm[KPP]*0.4    coef_corl = complex(1+0j)    for K1,K2,K3,K4 in zip(COMP_DATAIN_1K,COMP_DATAIN_2K,COMP_DATAIN_3K,COMP_DATAIN_4K):        K_OSN = (K1+K2+K3+K4)*coef_corl        COMP_DATAIN_OSN.append(K_OSN)    COMP_DATAIN_OSN_FKM = COMP_DATAIN_OSN[:210]    COMP_DATAIN_OSN_LFM = COMP_DATAIN_OSN[:]    COMP_OSN_LFM_SG = COMP_LFM.comp_lfm_sg(COMP_DATAIN_OSN_LFM)[0]    COMP_OSN_FKM_SG = sg_fkm.COMP_FKM_SG(COMP_DATAIN_OSN_FKM,r.barker_('n7'))[2]    COMP_OSN_FKM_SG = COMP_OSN_FKM_SG[:198]    #print('COMP_OSN_LFM_SG',type(COMP_OSN_LFM_SG))    COMP_P_SG = COMP_OSN_FKM_SG + COMP_OSN_LFM_SG    #SIGN_OSN = tran.from_COMP_in_RE_IM(COMP_P_SG)    #lfm = tran.from_COMP_in_RE_IM(COMP_OSN_LFM_SG)    #osn = tran.from_COMP_in_RE_IM(COMP_DATAIN_OSN_LFM)    '''    print(COMP_DATAIN_1K)    print(COMP_DATAIN_2K)    print(COMP_DATAIN_3K)    print(COMP_DATAIN_4K)    print(COMP_DATAIN_OSN)    '''    MODUL = []    for i in COMP_P_SG:        #M = 20 * math.log10(abs(i))        M = abs(i)        MODUL.append(M)    print('end_POLNAYA')    return COMP_P_SG, MODUL, COMP_DATAIN_OSN_LFM#POLNAYA('data_EF_RI1.dat')def POLNAYA_1(name_file):    print('POLNAYA_1')    #DATAIN = r.READ_FILE_HEX_16r_shapka20(name_file)    DATAIN = r.READ_FILE_HEX_16r_bez_sh(name_file)    COMP_DATAIN = DATAIN[:]    coef_fkm = 5.3452,5.8116,6.0749,6.3923,5.3452,6.3278,6.8020,7.7305    coef_lfm = 1.0000,1.0872,1.1365,1.1959,1.0000,1.1838,1.1273,1.4462    coef_fkm = coef_fkm[0]*1.0    coef_lfm = coef_lfm[0]*0.4    COMP_DATAIN_OSN_FKM = COMP_DATAIN[:210]    COMP_DATAIN_OSN_LFM = COMP_DATAIN[409:]    COMP_OSN_LFM_SG = COMP_LFM.comp_lfm_sg(COMP_DATAIN_OSN_LFM)[0]    COMP_OSN_FKM_SG = sg_fkm.COMP_FKM_SG(COMP_DATAIN_OSN_FKM,r.barker_('n7'))[2]    COMP_OSN_FKM_SG = COMP_OSN_FKM_SG[:198]    #print('COMP_OSN_LFM_SG',type(COMP_OSN_LFM_SG))    COMP_P_SG = COMP_OSN_FKM_SG + COMP_OSN_LFM_SG    #SIGN_OSN = tran.from_COMP_in_RE_IM(COMP_P_SG)    #lfm = tran.from_COMP_in_RE_IM(COMP_OSN_LFM_SG)    #osn = tran.from_COMP_in_RE_IM(COMP_DATAIN_OSN_LFM)    MODUL = []    for i in COMP_P_SG:        M = 20 * math.log10(abs(i))        #print(M)        MODUL.append(M)    print('end_POLNAYA')    return COMP_P_SG, COMP_DATAIN_OSN_LFM, MODUL